pragma solidity ^0.6.0; // boleh tukar ke 0.5.0
pragma experimental ABIEncoderV2;

contract Escroll
{
    
    mapping(address => University) public universities;
    struct University
    {
        address uniAddress;
        string uniName;
        string[] matricNo;
        string status;
        
        uint deployTime;
        uint blockNumber;
    }
    
    event universitiesInserted
    (
        address uniAddress,
        string uniName,
        string[] matricNo,
        string status,
        
        uint deployTime,
        uint blockNumber
    );    
    
    // boleh tambah status untuk tahu dia dah updated or added
    // Kat sini maybe boleh letak name, matric number, gender, nationality dengan IC, kat scroll boleh letak detail pasal course, faculty kalau2 uni nak lebih dari 1 bahasa.
    mapping(string => Student) public students;
    struct Student
    {
        address uniAddress;
        string uniName;
        
        string name;
        string matricNumber;
        string nationality; //citizenship
        string faculty; //kulliyah
        string course; // programme
        string identityCardNo; // Keep in view
        // uint birtDate; // KIV
        string gender;
        
        string status;
        uint deployTime;
        uint blockNumber;
    }
    

    
    event studentInserted
    (
        address uniAddress,
        string uniName,
        
        
        string name,
        string matricNumber,
        string nationality,
        string faculty,
        string course,
        string identityCardNo, // Keep in view
        string gender,
        
        string status,
        uint deployTime,
        uint blockNumber
    );
    
    event studentUpdated
    (
        address uniAddress,
        string uniName,
        
        
        string name,
        string matricNumber,
        string nationality,
        string faculty,
        string course,
        string identityCardNo, // Keep in view
        string gender,
        
        string status,
        uint deployTime,
        uint blockNumber
    );
    
    address contractOwner;
    constructor() public
    { 
        //boleh tukar jadi Owner = (address KPT) so nanti KPT ada power untuk add/delet participant
        contractOwner = msg.sender;
        
    }
    
    
    
    //Untuk verify University
    function verifyUniversity(address _sender) public view returns(bool)
    {
        if(universities[_sender].uniAddress == msg.sender )
        {
            return true;
        }
    }
    
    
    //Untuk verify University yang add student tu
    function verifyOwner(address _sender, string memory _matNo) public view returns(bool)
    {
        if(universities[_sender].uniAddress == msg.sender)
        {
            if(students[_matNo].uniAddress == msg.sender)
            {
                return true;
            }
        }
        else
        {
            return false;
        }
        
    }
    
    
    
    
    //Untuk fetch data pasal Uni dan matric number
    address[] public allUniAdd; // maybe boleh tukar ke addres dan uni name
    string[] public allUniName;
    function getAllUniversity() public view returns(address[] memory, string[] memory)
    {
        return (allUniAdd, allUniName);
    }
    
    // function getAllUniversityAddress() public view returns(address[] memory)
    // {
    //     return allUniAdd;
    // }
    
    //matric number untuk student dalam uni tu
    //Get kat front end kena letak from address mana
    function getAllStudentMatricNumberInTheUniversity() public view returns(string[] memory) 
    {
        return universities[msg.sender].matricNo; 
    }
    
    
    
    
    //Cari individual student
    // kena specific dengan lower or uppercase
    function getStudent(string memory _matricNumber) public view returns(address, string memory, string memory, string memory, string memory, string memory, string memory, string memory, string memory, string memory, uint, uint)
    {
        string memory number = _matricNumber;
        return (students[number].uniAddress, students[number].uniName, students[number].name , students[number].matricNumber , students[number].nationality , students[number].faculty , students[number].course, students[number].identityCardNo, students[number].gender, students[number].status , students[number].deployTime, students[number].blockNumber);
    }
    
    function getUniversity(address _uniAdd) public view returns(address, string memory, string memory)
    {
        address addr = _uniAdd;
        if(universities[_uniAdd].uniAddress == _uniAdd)
        {
            return (universities[addr].uniAddress, universities[addr].uniName, universities[addr].status);
        }
        else
        {
            if(bytes(universities[_uniAdd].status).length > 5)
            {
                return (universities[addr].uniAddress, universities[addr].uniName, universities[addr].status);
            }
            else
            {
                revert("That University is not in the system");
            }
        }
        
        
    }
    
    


    //Add university
    function insertUniversity(address _uniAddress, string memory _uniName) public
    {
        if (msg.sender == contractOwner)
        {
            allUniAdd.push(_uniAddress);
            allUniName.push(_uniName);
            string[] memory empty;
            string memory _status = 'Added';
            
            
            universities[_uniAddress] = University(_uniAddress, _uniName, empty, _status, block.timestamp, block.number);
            
            emit universitiesInserted(_uniAddress, _uniName, empty, _status, block.timestamp, block.number);
            
        }
        else
        revert ("You do not have the credential to do such action as you are not the contract owner");
        
        
    }
    
    function universityCounter(address _uniAddress) public view returns(uint)
    {
        uint counter; 
        for (uint i=0; i <= allUniAdd.length; i++)
        {
            if(allUniAdd[i] == _uniAddress)
            {
                counter = i;
                return counter;
            }
        }
    }
    
    //Delete University
    function deleteUniversity(address _uniAddress) public
    {

        
        if (msg.sender == contractOwner)
        {
            uint counter = universityCounter(_uniAddress);
            
            delete allUniAdd[counter];
            delete allUniName[counter];
            delete universities[_uniAddress];
            universities[_uniAddress].status = 'Deleted';
            universities[_uniAddress].blockNumber = block.number;
            universities[_uniAddress].deployTime = block.timestamp;
        }
        else
        revert ("You do not have the credential to do such action as you are not the contract owner");
        // delete universities[_uniAddress];
        
        
    }
    

    //Add student
    function insertStudent(string memory _name, string memory _matricNumber, string memory _nationality, string memory _faculty, string memory _course, string memory _ic, string memory _gender) public
    {
        // address alamatUni = universities[msg.sender].uniAddress;
        bool ada = verifyUniversity(msg.sender);

 
        if(ada == true)
        {

            if (bytes(_name).length <= 0 || bytes(_matricNumber).length <= 0 || bytes(_nationality).length <= 0 || bytes(_faculty).length <= 0 
            || bytes(_gender).length <= 0 || bytes(_course).length <= 0)
            {
                revert("Not enough details");
            }
            
            // Pakai keccak
            if(keccak256(abi.encodePacked((students[_matricNumber].matricNumber))) == keccak256(abi.encodePacked((_matricNumber))) )
            revert("Student already exist");
            
            string memory namaUni = universities[msg.sender].uniName;
            string memory _status = 'Added';
            
            
            universities[msg.sender].matricNo.push(_matricNumber);
            students[_matricNumber] = Student(msg.sender, namaUni, _name, _matricNumber, _nationality, _faculty, _course, _ic, _gender, _status, block.timestamp, block.number);
            
            emit studentInserted(msg.sender, namaUni,  _name, _matricNumber, _nationality, _faculty, _course, _ic, _gender, _status, block.timestamp, block.number);
        }
        else
        revert("Your university address is not listed in the system");
    }
    
   
    function matricNoCounter(string memory _matNo) public view returns(uint)
    {
        uint counter;
        string[] memory matricNo = universities[msg.sender].matricNo;
        for (uint i=0; i <= matricNo.length; i++)
        {
            if(keccak256(abi.encodePacked((matricNo[i]))) == keccak256(abi.encodePacked((_matNo))))  
            {
                counter = i;
                return counter;
            }
        }
    }
    
    //DIorang tak nak delete features
    //Delete student
    function deleteStudent(string memory _matricNumber) public
    {
        bool trueOwner = verifyOwner(msg.sender, _matricNumber);
        // address alamatUni = universities[msg.sender].uniAddress;
        bool ada = verifyUniversity(msg.sender);
        
        if(ada == true)
        {
            if(trueOwner == true)
            {
                uint counter = matricNoCounter(_matricNumber);
                delete students[_matricNumber];
            
                delete universities[msg.sender].matricNo[counter];
                students[_matricNumber].status = 'Deleted';
                students[_matricNumber].deployTime = block.timestamp;
                students[_matricNumber].blockNumber = block.number;
                students[_matricNumber].matricNumber = _matricNumber;
            }
            else
            revert("This student record is not from your university");            
        }
        else
        revert("Your university address is not listed in the system");
       
    }
    
    
    //Update student
    function updateStudent(string memory _name, string memory _matricNumber, string memory _nationality, string memory _faculty, string memory _course, string memory _ic, string memory _gender) public
    {
        bool trueOwner = verifyOwner(msg.sender, _matricNumber);
        // address alamatUni = universities[msg.sender].uniAddress;


        if(trueOwner == true)
            {
                if (bytes(_name).length <= 0 || bytes(_matricNumber).length <= 0 || bytes(_nationality).length <= 0 || bytes(_faculty).length <= 0 
                || bytes(_gender).length <= 0 || bytes(_course).length <= 0)
                {
                     revert("Not enough details");
                }
                
                string memory namaUni = universities[msg.sender].uniName;
                string memory _status = 'Updated';

                students[_matricNumber] = Student(msg.sender, namaUni, _name, _matricNumber, _nationality, _faculty, _course, _ic, _gender, _status, block.timestamp, block.number);
                
                emit studentUpdated(msg.sender, namaUni, _name, _matricNumber, _nationality, _faculty, _course, _ic, _gender, _status,block.timestamp, block.number);
            }
            else
            revert("This student record is not from your university");
        
    }
    


    
    mapping(string => Scroll) public scroll;
    struct Scroll
    {
        string matricNumber;
        string scrollIPFS;
        
        string status;
        uint deployTime;
        uint blockNumber;
    }
    
    event scrollInserted
    (
        string matricNumber,
        string scrollIPFS,
        
        string status,
        uint deployTime,
        uint blockNumber
    );
    
    event scrollUpdated
    (
        string matricNumber,
        string scrollIPFS,
        
        string status,
        uint deployTime,
        uint blockNumber
    );
    
    
    function getStudentIPFSScroll(string memory _matricNumber) public view returns(string memory)
    {
        return (scroll[_matricNumber].scrollIPFS );
    }
    
    function insertScroll(string memory _matricNumber, string memory _ipfsHash) public
    {
        bool trueOwner = verifyOwner(msg.sender, _matricNumber);

        if(trueOwner == true)
        {
            if (bytes(_matricNumber).length <= 0 || bytes(_ipfsHash).length <= 0)
            {
                 revert("Not enough details");
            }
            
            string memory _status = 'Added';
            
            scroll[_matricNumber] = Scroll(_matricNumber, _ipfsHash, _status, block.timestamp, block.number);
            emit scrollInserted(_matricNumber, _ipfsHash, _status, block.timestamp, block.number);
            
        }
        else
        revert("This student record is not from your university");
    }
    
    function updateScroll(string memory _matricNumber, string memory _ipfsHash) public
    {
        bool trueOwner = verifyOwner(msg.sender, _matricNumber);


        if(trueOwner == true)
        {
            if (bytes(_matricNumber).length <= 0 || bytes(_ipfsHash).length <= 0)
            {
                 revert("Not enough details");
            }
            
            string memory _status = 'Updated';
            
            scroll[_matricNumber] = Scroll(_matricNumber, _ipfsHash, _status, block.timestamp, block.number);
            emit scrollUpdated(_matricNumber, _ipfsHash, _status, block.timestamp, block.number);
            
        }
        else
        revert("This student record is not from your university");
    }
    
    
